    <div class="wp-block-qubely-accordion-item qubely-block-2019fb"><div class="qubely-accordion-item qubely-type-fill false"><div class="qubely-accordion-panel qubely-icon-position-right"><span class="qubely-accordion-panel-handler" role="button"><span class="qubely-accordion-panel-handler-label">Do IOU certificates expire?</span><span class="qubely-accordion-icon fa fa-plus"></span></span></div><div class="qubely-accordion-body"><div itemprop="text"><!-- wp:paragraph -->
                    <p>No, they don't expire and you can put them in your CV/resume.</p>
                    <!-- /wp:paragraph --></div></div></div></div>
    <!-- /wp:qubely/accordion-item -->

    <!-- wp:qubely/accordion-item {"uniqueId":"8588c8","itemNumber":8,"heading":"Can I retake a course complition exam/quiz?","panelColor":"var(\u002d\u002dqubely-color-3)","panelBg":{"openColor":1,"type":"color","color":"rgba(254,241,243,0.75)","gradient":{"color1":"#EEEEEE","color2":"#e5e5e5","direction":0,"start":0,"stop":100,"type":"linear"}},"panelBgActive":{"openColor":1,"type":"color","color":"#fc8ba1","gradient":{"color1":"#2476CA","color2":"#1A5FA4","direction":0,"start":0,"stop":100,"type":"linear"}},"typography":{"openTypography":false},"panelBorderRadius":{"radiusType":"global","global":{"md":"3"},"unit":"px","openBorderRadius":1},"bodyBg":{"openColor":1,"type":"color","color":"rgba(239,239,239,0.18)","gradient":{"color1":"#f2f2f2","color2":"#e5e5e5","direction":0,"start":0,"stop":100,"type":"linear"}},"borderRadius":{"radiusType":"global","global":{"md":""},"unit":"px","openBorderRadius":1},"spacing":{"md":"25","unit":"px"}} -->
